content:
.badge {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: fit-content;
    white-space: nowrap;
    flex-shrink: 0;
    overflow: hidden;
    background-color: var(--color-fill-loud, var(--color-neutral-fill-loud));
    color: var(--color-on-loud, var(--color-neutral-on-loud));
    border-radius: var(--radius-full);
    border-style: var(--border-style);
    border-width: var(--border-width-thin);
    border-color: transparent;
    padding: 0 0.75em;
    font-size: max(var(--text-xs), 0.75em);
    font-weight: var(--font-weight-medium);
    gap: var(--space-2xs);
    transition: color var(--transition-normal), box-shadow var(--transition-normal);
}
