content:
.dialog:has(.command) {
    padding: 0;
    border: none;
}

.dialog-backdrop {
    position: fixed;
    inset: 0;
    z-index: var(--z-modal);
    background-color: var(--color-overlay);
    animation: dialog-fade-out var(--transition-normal) var(--ease-out);
}

.dialog-backdrop[data-state="open"] {
    animation: dialog-fade-in var(--transition-normal) var(--ease-out);
}

.dialog {
    position: fixed;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    z-index: var(--z-modal);
    max-width: calc(100vw - 2rem);
    width: 26rem;
    background-color: var(--color-surface-raised);
    padding: var(--space-md);
    box-shadow: var(--shadow-md);
    border-radius: var(--panel-radius);
    animation: dialog-close var(--transition-normal) var(--ease-out);
}

.dialog[data-state="open"] {
    animation: dialog-open var(--transition-normal) var(--ease-out);
}

@keyframes dialog-fade-in {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

@keyframes dialog-fade-out {
    from {
        opacity: 1;
    }
    to {
        opacity: 0;
    }
}

@keyframes dialog-open {
    from {
        opacity: 0;
        transform: translate(-50%, -48%) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translate(-50%, -50%) scale(1);
    }
}

@keyframes dialog-close {
    from {
        opacity: 1;
        transform: translate(-50%, -50%) scale(1);
    }
    to {
        opacity: 0;
        transform: translate(-50%, -48%) scale(0.95);
    }
}
