content:
[data-slot="popover-trigger"] svg {
    pointer-events: none;
    color: var(--color-text-quiet);
}

.popover-content {
    outline: none;
    min-width: fit-content;
    background-color: var(--color-surface-raised);
    transform-origin: var(--radix-popover-content-transform-origin);
    border-width: var(--panel-border-width);
    border-style: var(--panel-border-style);
    border-color: var(--panel-border-color);
    border-radius: var(--panel-radius);
    padding-inline: var(--space-md);
    padding-block: var(--space-md);
    box-shadow: var(--shadow-xs);
}

.popover-content:has(.command) {
    padding: 0;
}

.popover-content[data-state="open"] {
    animation: popover-slide-in-from-top var(--transition-normal) var(--ease-out);
}

.popover-content[data-side="bottom"] {
    animation: popover-slide-in-from-top var(--transition-normal) var(--ease-out);
}

.popover-content[data-side="left"] {
    animation: popover-slide-in-from-right var(--transition-normal) var(--ease-out);
}

.popover-content[data-side="right"] {
    animation: popover-slide-in-from-left var(--transition-normal) var(--ease-out);
}

.popover-content[data-side="top"] {
    animation: popover-slide-in-from-bottom var(--transition-normal) var(--ease-out);
}

@keyframes popover-slide-in-from-top {
    from {
        opacity: 0;
        transform: translateY(-8px) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

@keyframes popover-slide-in-from-bottom {
    from {
        opacity: 0;
        transform: translateY(8px) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

@keyframes popover-slide-in-from-left {
    from {
        opacity: 0;
        transform: translateX(-8px) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translateX(0) scale(1);
    }
}

@keyframes popover-slide-in-from-right {
    from {
        opacity: 0;
        transform: translateX(8px) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translateX(0) scale(1);
    }
}
